mapper.update:
		UPDATE 
			curriculum
		SET    		 
   			id_area=#{area.id}, uri=#{uri}, state=#{state}, city=#{city}
   			,contact=#{contact}, search=#{search}, brief_description=#{brief_description}, description=#{description}
   		WHERE id_people=#{people.id};

mapper.select:
    SELECT pe.name, pe.avatar, cu.uri, pe.id_country, cu.state, cu.brief_description 
FROM 
  curriculum cu JOIN people pe on pe.id = cu.id_people
WHERE 
  true = true

  	AND pe.id_role = #{role}


  	AND cu.id_area = #{area}


  	AND LOWER(cu.state) like LOWER('%' || #{state} || '%') 


  	AND LOWER(cu.search) like LOWER('%' || #{search} || '%') 


  	AND pe.id_country = #{country}
