mapper.update:
		UPDATE 
			people
		SET    		 
   			name=#{name}, avatar=#{avatar}, register=#{register}, access=#{access}
   			,id_country=#{country.id}, id_role=#{role.id}, id_language=#{language.id}
   		WHERE id=#{id};

		UPDATE 
			people
		SET    		 
   			email=#{email}, password=#{password}
   		WHERE id=#{id};

mapper.select:

where true = true

		AND LOWER(name) like LOWER('%' || #{name} || '%') 


		AND email = #{email}


		AND id_role = #{role}


		AND id_language = #{language}


		AND id_country = #{country}
